package com.sampleprograms.operators;

/**
 * @author Sandesh
 *
 */
public class ArithmeticOperators {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 24;
		int num2 = 67;
		
		System.out.println(" sum of two numbers " + (num1 + num2));
		System.out.println(" Difference of two numbers " + (num2 - num1));
		System.out.println(" Multiplication of two numbers " + (num1 * num2));
		System.out.println(" Division of two numbers " + (num2 / num1));
		System.out.println(" Modulus of the numbers " + (num2 % num1));
		System.out.println(" Pre-Increment operator " + (++num1));
		System.out.println(" Pre-Decrement operator " + (--num2));
		System.out.println(" Post-Increment operator " + (num1++));
		System.out.println(" Post-Decrement operator " + (num2--));
	}

}
