public class CircularArea {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float pi = 3.14f;
		float r = 6.8f;
		float area;
		area = pi * r * r;
		System.out.println("the area of the circle is " + area);
		
	}

}
